var EnvironCount = 5;
var MedCount = 5;
var CrimCount;
var NanCount;
var pie;

function pi(){
    $.ajax({ 
        type: 'GET', 
        url: 'GetCount.php', 
        data: { get_param: '$CrimCnt' }, 
        success: function (data) { 
            var data = data.split(",");
            CrimCount = parseInt(data[0]);
            MedCount = parseInt(data[1]);
            EnvironCount = parseInt(data[2]);
            NanCount = parseInt(data[3]);
            AttendedCount = parseInt(data[4]);
            InProgressCount = parseInt(data[5]);
            UnattendedCount = parseInt(data[6]);
            TotalCount = parseInt(data[7]);

            $("#CntUnattended").text(UnattendedCount);
            $("#CntProgress").text(InProgressCount);
            $("#CntAttended").text(AttendedCount);
            $("#CntAll").text(TotalCount);

            pie = d3pie("pieChart", {
				"header": {
					"title": {
						"text": "Type of Incident",
						"color": "#fff",
						"fontSize": 20,
						"font": "verdana"
					},
					"subtitle": {
						"text": "            ",
						"color": "#fff",
						"fontSize": 9,
						"font": "verdana"
					},
					"titleSubtitlePadding": 12
				},
				"footer": {
					"color": "#999999",
					"fontSize": 11,
					"font": "open sans",
					"location": "bottom-center"
				},
				"size": {
					"canvasHeight": 355,
					"canvasWidth": 300,
					"pieInnerRadius": "60%",
					"pieOuterRadius": "100%"
				},
				"data": {
					"content": [
						{
							"label": "",
							"value": EnvironCount,
							"color": "#7e3838"
						},
						{
							"label": "",
							"value": CrimCount,
							"color": "#7e6538"
						},
						{
							"label": "",
							"value": MedCount,
							"color": "#7c7e38"
						},
						{
							"label": "",
							"value": NanCount,
							"color": "#a19898"
						}
					]
				},
				"labels": {
					"outer": {
						"pieDistance": 16
					},
					"inner": {
						"format": "value"
					},
					"mainLabel": {
						"font": "verdana",
						"color": "#fff",
					},
					"percentage": {
						"color": "#fff",
						"font": "verdana",
						"decimalPlaces": 0
					},
					"value": {
						"color": "#fff",
						"font": "verdana",
						"fontSize": 14
					},
					"lines": {
						"enabled": true,
						"color": "#cccccc"
					},
					"truncation": {
						"enabled": true
					}
				},
				"effects": {
					"pullOutSegmentOnClick": {
						"effect": "none",
						"speed": 400,
						"size": 8
					}
				},
				"misc": {
					"colors": {
						"background": "#0a1330"
					}
				},
				"callbacks": {
					onMouseoverSegment: function(info) {
						//console.log("mouseover:", info);
					},
					onMouseoutSegment: function(info) {
						//console.log("mouseout:", info);
					}
				}
			});


			complete: ws.onmessage = function(msg) {
				    	//WFSLayer.remove();
				    	onLoadd();
                  		$.notify("There has been a change to the table", { position:"bottom left" });

				    	$("#pieChart").empty();
	    				pi();

	    				var snd = new  Audio("data:audio/wav;base64,");
                  		snd.play();
				};

        }
    });
};



